function loadDatabase() {
	//create a connection to the service providing the database
    var httpclient = new kony.net.HttpRequest();
    //open this connection with a specific type: GET or POST, leave the last param as false
    httpclient.open(constants.HTTP_METHOD_GET, "https://littlesuitcase-7735.restdb.io/rest/useraccounts", false);
  	//set the header for the HTTP request, this will not have to change
  	httpclient.setRequestHeader("x-apikey", "1368977aab130f9a6e6ec87cbb08c152ad458");
  	try {
      httpclient.send();
      var response = httpclient.response;

      var lblLayout ={containerWeight:50, padding:[5,5,5,5], margin:[5,5,5,5], hExpand:true, vExpand:false};
    var lblLayout2 ={renderAsAnchor:true, wrapping:constants.WIDGET_TEXT_WORD_WRAP};

      var nationality = '';
      var age = '';
      var birthdate = '';
      var luggage = '';
      var photo = '';
      var id = '';

      var y =100;

      for (var profile in response) {
        for (var i in profile) {
          if (i == "Nationality") nationality = profile[i];
          if (i == "Age") age = profile[i];
          if (i == "Birthdate") birthdate = profile[i];
          if (i == "Luggage Description") luggage = profile[i];
          if (i == "Photo") photo = profile[i];
          if (i =="_id") id = profile[i];
        }
        //assign the variables to the UI
        labelText = nationality;
        var lblBasic = {id:id, skin:"lblSkn", text:labelText, isVisible:true};
        var lbl = new kony.ui.Label(lblBasic, lblLayout, lblLayout2);//create on runtime a label (could be another UI element) to insert the messages
        y +=100;
        lbl.centerY =y + "px";
        lbl.addGestureRecognizer (constants.GESTURE_TYPE_TAP,{fingers: 1}, gestureCB);// could use instead: GESTURE_TYPE_SWIPE
        this.view.add(lbl);
      }
    } 
  	catch(err) {
    //show a message with the error
    }
 }

loadDatabase();
